INSERT INTO specialization (name, description) VALUES ('Ophthalmology', 'Ophthalmology is the branch of medicine that deals with the diagnosis and treatment of eye disorders. Ophthalmologists diagnose and treat conditions such as cataracts, glaucoma, and macular degeneration.');
INSERT INTO specialization (name, description) VALUES ('Orthopedics', 'Orthopedics is the branch of medicine that deals with the diagnosis and treatment of disorders of the musculoskeletal system. Orthopedists diagnose and treat conditions such as broken bones, arthritis, and spine disorders.');
INSERT INTO specialization (name, description) VALUES ('Otolaryngology', 'Otolaryngology, also known as ENT (Ear, Nose, and Throat) deals with the diagnosis and treatment of disorders of the head and neck. Otolaryngologists diagnose and treat conditions such as hearing loss, sinusitis, and sleep apnea.');
INSERT INTO specialization (name, description) VALUES ('Pediatrics', 'Pediatrics is the medical specialty that deals with the healthcare of children, from birth through adolescence. Pediatricians provide preventative care, treat acute and chronic illnesses, and coordinate care with other specialists.');
INSERT INTO specialization (name, description) VALUES ('Psychiatry', 'Psychiatry is the branch of medicine that deals with the diagnosis and treatment of mental illnesses. Psychiatrists diagnose and treat conditions such as depression, anxiety, and schizophrenia using therapy and medications.');
INSERT INTO specialization (name, description) VALUES ('Pulmonology', 'Pulmonology is the study and treatment of diseases of the respiratory system. Pulmonologists diagnose and treat conditions such as asthma, chronic obstructive pulmonary disease (COPD), and lung cancer.');
INSERT INTO specialization (name, description) VALUES ('Radiology', 'Radiology is the branch of medicine that deals with medical imaging to diagnose and treat diseases. Radiologists use a variety of imaging techniques including X-rays, CT scans, and MRI scans to diagnose and monitor conditions.');
INSERT INTO specialization (name, description) VALUES ('Rheumatology', 'Rheumatology is the study and treatment of diseases that affect the joints, muscles, and bones. Rheumatologists diagnose and treat conditions such as arthritis, lupus, and osteoporosis.');
INSERT INTO specialization (name, description) VALUES ('Surgery', 'Surgery is a medical specialty that involves using operative techniques to diagnose and treat diseases, injuries, and deformities. Surgeons may specialize in a specific type of surgery, such as neurosurgery or cardiothoracic surgery.');
INSERT INTO specialization (name, description) VALUES ('Urology', 'Urology is the study and treatment of diseases of the urinary tract and male reproductive system. Urologists diagnose and treat conditions such as urinary tract infections, kidney stones, and prostate cancer.');
INSERT INTO specialization (name, description) VALUES ('Emergency Medicine', 'Emergency Medicine is the specialty that focuses on the immediate care of patients who require urgent medical attention. Emergency Medicine physicians diagnose and treat conditions such as heart attacks, strokes, and trauma.');
INSERT INTO specialization (name, description) VALUES ('Endocrinology', 'Endocrinology is the study and treatment of diseases of the endocrine system, which includes glands that produce hormones. Endocrinologists diagnose and treat conditions such as diabetes, thyroid disorders, and osteoporosis.');
